package fi.unju.edu.ar.enunciado10;

import java.util.Scanner;

public class SecuenciaWhile {

    public static void main(String[] args) {
        // TODO Auto-generated method stub
        int num=320;
        while(num>160)
        {
            num=num-20;
            System.out.println(num);
        }
    }

}
